import ahocorasick

class a:
    def __init__(self):
        key_list = ["苹果", "香梨", "橙子", "柚子", "香蕉", "柿子"]
        self.actree=self.build_actree(key_list)
    def build_actree(self, wordlist):
        actree = ahocorasick.Automaton()
        for index, word in enumerate(wordlist):
            actree.add_word(word, (index, word))
        actree.make_automaton()
        return actree
    def check_actree(self,target_str):
        for i in self.actree.iter(target_str):
            wd = i[1][1]
            print(i[0])

if __name__=='__main__':
    py=a()
    py.check_actree('我最喜欢吃柚子和香蕉')